import { useContext, useEffect, useState } from 'react'

import { UserContext } from '@/contexts/UserContext'

const Header = () => {
  const { user, setUser } = useContext(UserContext);
  const [isRootPath, setIsRootPath] = useState(false);

  const imageUrl = 'https://res.cloudinary.com/dao5kgzkm/image/upload/logo';

  useEffect(() => {
    // Check if the current path is the root path
    const checkPath = () => {
      const path = window.location.pathname;
      setIsRootPath(path !== '/');
    };

    // Check initially
    checkPath();

    // Set up listener for route changes
    const handleRouteChange = () => {
      checkPath();
    };

    window.addEventListener('popstate', handleRouteChange);

    return () => {
      window.removeEventListener('popstate', handleRouteChange);
    };
  }, []);

  const handleLogout = () => {
    setUser(null);
  };

  return (
    <header className='fixed bg-blue-darkest h-16 w-full z-50'>
      <div className='flex items-center h-full px-4 py-3 md:space-x-5'>
        <img alt='Logo de CompraFacil' className='h-full md:block hidden' crossOrigin='anonymous' src={imageUrl} />
        <span className='text-white text-xl font-bold md:block hidden'>CompraFácil</span>
        <hr className='h-full w-0.5 blue bg-white md:block hidden' />
        {/* Justification:
      https://www.w3.org/WAI/WCAG22/Techniques/aria/ARIA11.html */}
        <nav>
          <ul className='flex flex-row-reverse items-center space-x-4 space-x-reverse'>
            {!isRootPath && (
              <li className='text-white text-xl'>
                <a href='assistance'>Ayuda</a>
              </li>
            )}
            <li className='text-white text-xl'>
              <a href='/blog'>Blog</a>
            </li>
            <li className='text-white text-xl'>
              <a href='/'>Catálogo</a>
            </li>
          </ul>
        </nav>
      </div>

      <div className='absolute right-0 top-0 h-full flex items-center px-4 py-3 space-x-5'>
        <a className='h-full' href='/payment/cart'>
          <img alt='Tu carrito de compras' className='h-full' crossOrigin='anonymous' src='/assets/vectors/shopping_cart.svg' />
        </a>

        {user ? (
          <button
            className='bg-blue-light h-full px-3 font-semibold text-lg rounded-md hover:bg-blue-medium-light'
            onClick={handleLogout}>
            Cerrar sesión
          </button>
        ) : (
          <a className='flex items-center justify-center bg-blue-light h-full px-3 font-semibold text-lg rounded-md hover:bg-blue-medium-light'
            href='/login'
            onFocus={() => window.location.href = '/login'}>
            Iniciar sesión
          </a>
        )}
      </div>
    </header>
  )
}

export default Header

import { useState, useEffect } from 'react'

import posts from '@/utils/posts'

import Post from './Post'

const Blog = () => {
  // State to control blinking animation
  const [isBlinking, setIsBlinking] = useState(true);

  // Effect to stop blinking after 30 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsBlinking(false);
    }, 30000); // 30000 milliseconds = 30 seconds

    // Clean up timer on unmount
    return () => clearTimeout(timer);
  }, []);

  return (
    <section>
      <h1 className='text-6xl text-blue-darkest pb-8 text-center font-bold'>Blog</h1>
      <div className='flex flex-col space-y-6'>
        {posts.map((post, index) => (
          <Post
            key={index}
            content={post.content}
            id={index}
            title={post.title}
          />))}
      </div>

      {/* Mensaje promocional con animación de parpadeo usando Tailwind */}
      {/* Note: Animation property saves the blinking interval time */}
      <div 
        className={`mt-10 mx-auto max-w-4xl border-2 border-blue-dark rounded-lg p-6 shadow-md
          ${isBlinking ? 'animate-blink' : 'bg-blue-light'}`}
      >
        <h2 className='text-2xl font-bold text-center mb-3'>
          ¡Promociones de Verano Disponibles Ahora!
        </h2>
        <p className='text-lg text-center mb-4'>
          Disfruta de descuentos increíbles en toda nuestra colección de verano.
          Renueva tu armario con las últimas tendencias a precios imbatibles.
        </p>
      </div>
    </section>
  )
}

export default Blog

const Post = (props) => {

  const { id, title, content } = props;

  // Function to get the substring without cutting off words
  const getSubstring = (text, maxLength) => {
    if (text.length <= maxLength) return text;
    const trimmedText = text.substring(0, maxLength);
    const lastSpaceIndex = trimmedText.lastIndexOf(' ');
    return trimmedText.substring(0, lastSpaceIndex) + '...';
  };

  return (
    <article className='min-h-auto h-auto w-full px-10 py-6 bg-blue-medium-light'>
      <h2 className='text-2xl font-bold mb-2'>{title}</h2>
      <p className='text-lg mb-4'>{getSubstring(content, 150)}</p>
      <a
        className='flex items-center justify-center h-9 w-28 bg-blue-dark text-white text-xl mt-3 hover:bg-blue-darkest'
        href={'/blog/post' + (id + 1)}>Leer más</a>
    </article>
  )
}

export default Post

import { ChevronLeft, ChevronRight } from 'lucide-react';
import { useState, useRef } from 'react';

const Carousel = (props) => {
  const { images, description } = props;

  const [index, setIndex] = useState(0);
  const imageUrl = 'https://res.cloudinary.com/dao5kgzkm/image/upload/v1741316071/Clothing/';
  
  // Refs for drag functionality
  const carouselRef = useRef(null);
  const startXRef = useRef(0);
  const currentXRef = useRef(0);
  const isDraggingRef = useRef(false);
  const threshold = 50; // Minimum drag distance to trigger a slide change
  
  const goToPrevious = () => {
    setIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };
  
  // Mouse events for desktop
  const handleMouseDown = (e) => {
    isDraggingRef.current = true;
    startXRef.current = e.clientX;
    currentXRef.current = e.clientX;
  };
  
  const handleMouseMove = (e) => {
    if (isDraggingRef.current) {
      currentXRef.current = e.clientX;
    }
  };
  
  const handleMouseUp = () => {
    if (isDraggingRef.current) {
      const diff = currentXRef.current - startXRef.current;
      
      // If dragged far enough, change slide
      if (Math.abs(diff) > threshold) {
        if (diff > 0) {
          goToPrevious(); // Dragged right -> show previous image
        } else {
          goToNext(); // Dragged left -> show next image
        }
      }
      
      isDraggingRef.current = false;
    }
  };
  
  // Touch events for mobile
  const handleTouchStart = (e) => {
    isDraggingRef.current = true;
    startXRef.current = e.touches[0].clientX;
    currentXRef.current = e.touches[0].clientX;
  };
  
  const handleTouchMove = (e) => {
    if (isDraggingRef.current) {
      currentXRef.current = e.touches[0].clientX;
    }
  };
  
  const handleTouchEnd = () => {
    if (isDraggingRef.current) {
      const diff = currentXRef.current - startXRef.current;
      
      // If dragged far enough, change slide
      if (Math.abs(diff) > threshold) {
        if (diff > 0) {
          goToPrevious(); // Dragged right -> show previous image
        } else {
          goToNext(); // Dragged left -> show next image
        }
      }
      
      isDraggingRef.current = false;
    }
  };

  return (
    <div 
      ref={carouselRef}
      aria-label='Carrusel de imágenes del producto'
      aria-roledescription='Carrusel'
      className='relative h-full w-1/2 z-0'
      role='region'
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onTouchEnd={handleTouchEnd}
      onTouchMove={handleTouchMove}
      onTouchStart={handleTouchStart}
    >
      {/* Current image */}
      <img
        aria-label={`Imagen ${index + 1} de ${images.length}: ${description}`}
        aria-roledescription='Imagen'
        className='h-full w-full object-cover rounded-lg pointer-events-none'
        crossOrigin='anonymous'
        src={imageUrl + images[index] + '.jpg'}
      />
      
      {/* Add slide indicators */}
      <div className='absolute bottom-2 left-0 right-0 flex justify-center gap-2'>
        {images.map((_, i) => (
          <div
            key={i}
            className={`h-2 w-2 rounded-full ${i === index ? 'bg-blue-dark' : 'bg-white bg-opacity-60'}`}
          />
        ))}
      </div>
    </div>
  );
};

export default Carousel;

import { useState, useEffect } from 'react'

import products from '@/utils/products';

import Item from './Item';
import Music from './Music';
import Notification from './Notification';
import { ShoppingCartProvider } from '../../contexts/ShoppingCartContext';

const Catalog = () => {
  const [notification, setNotification] = useState('');

  const showNotification = (message) => setNotification(message);

  useEffect(() => {
    const handleKeyDown = (event) => {
      // Check if the pressed key is 'c' or 'C'
      if (event.key.toLowerCase() === 'c') {
        window.location.href = '/payment/cart';
      }
    };

    // Add event listener for keydown events
    window.addEventListener('keydown', handleKeyDown);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);
  
  return (
    <>
    <section>
      <div className='flex flex-col items-center md:items-start mb-4 md:mb-0'>
        <Music />
      </div>

      { /*(Section) Justification:
    https://developer.mozilla.org/en-US/docs/Web/HTML/Element/section#usage_notes */}
      <h1 className='text-6xl text-blue-darkest pb-8 text-center font-bold'>Catálogo</h1>

      {/* ¿Why not ul-li tags? TL;DR: Think a list as you always do, short elements and that's it
    https://stackoverflow.com/questions/16213214/is-it-a-good-practice-to-put-articles-inside-lis
    Posdata: Actually, there is a debate: https://stackoverflow.com/questions/19307443/semantic-html-of-an-articles-list */}
      <div className='grid lg:grid-cols-3 md:grid-cols-2 grid-cols-1 gap-8'>
        <ShoppingCartProvider>
          {products.map((product, index) => (
            <Item
              key={index}
              description={product.description}
              images={product.images}
              price={product.price}
              showNotification={showNotification}
              title={product.title}
            />
          ))}
        </ShoppingCartProvider>
      </div>

      <p className='text-center text-lg mt-4 py-6 bg-blue-medium-light border-4 border-blue-medium-dark rounded-lg'>
        <span className='font-bold'>Consejo:</span> ¡Puedes utilizar la tecla "C" para ir a tu carrito de compras!
      </p>
    </section>
    <Notification message={notification} onClose={() => setNotification('')} />
    </>
  )
}

export default Catalog

